import React, { useState, useCallback, useMemo, useEffect} from 'react'
import  { useFetch } from '../custom-hooks/useFetch'
// import './indexelements.js';
import { history } from '../helpers/history';

// ATTENTION!!!!!!!!!!
// I SWITCHED TO PERMANENT DOMAIN
//const url = 'http://localhost:8085/getAllItems'

//every time props or state changes, component re-renders
// const calculateMostExpensive = (data) => {
//   return (
//     data.reduce((total, item) => {
//       const price = item.base_price
//       if (price >= total) {
//         total = price
//       }
//       return total
//     }, 0) / 100
//   )
// }
const ShowItems = ({url}) => {
  console.log(url);
  const { products } = useFetch(url)

  return (
      <BigList products={products}/>
  )
}

const BigList = React.memo(({ products}) => {
  useEffect(() => {
    console.count('hello from big list');
  });

  return (
    <section className='products'>
      {products.map((product) => {
        return (
          <SingleProduct
            key={product.item_id}
            index={product.item_id}
            item_name= {product.item_name}
            base_price ={product.base_price}
            description= {product.description}
          ></SingleProduct>
        )
      })}
    </section>
  )
})

const SingleProduct = ({ index,item_name,base_price,description}) => {

    console.log(index);

    const [bid, setBid] = useState({
        price : 0
    })
    const [submitted, setSubmitted] = useState(false);


    function handleChange(e) {
        const { name, value } = e.target;
        setBid(bid => ({ ...bid, [name]: value }));
    }

    function handleSubmit ()
    {
        setSubmitted(true);
        console.log(bid.price)
        history.push("/home");
    }

    useEffect(() => {
        // console.count('hello from product');
    });
    return (
        <div className="col-md-12">
            <div className="card card-container">
                <article className='product'>
                <img src="https://www.shareicon.net/data/128x128/2015/09/23/106279_cart_512x512.png" alt={item_name} />
                <h4>{item_name}</h4>
                <p>Rs {base_price}</p>
                <p>{description}</p>
                <form name="form" onSubmit={handleSubmit}>

                    <div className="form-group">
                        <label>Your Price</label>
                        <input type="text" name="price" value={bid.price} onChange={handleChange} className={'form-control' + (submitted && !bid.price ? ' is-invalid' : '')} />
                        {submitted && bid.price &&
                            <div className="invalid-feedback">Base Price is required</div>
                        }
                    </div>

                    <div className="form-group">
                        <button className="btn btn-primary">
                            {/* {registering && <span className="spinner-border spinner-border-sm mr-1"></span>} */}
                            Make Bid
                        </button>
                            {/* <Link to="/login" className="btn btn-link">Cancel</Link> */}
                    </div>
                </form>
                </article>
            </div>
        </div>
  )
}
export default ShowItems
