import React, { useState, useCallback, useMemo, useEffect} from 'react'
import  { useFetch } from '../custom-hooks/useFetch'
import { history } from '../helpers/history';
import { itemService } from '../services/item.service';

const UserItems = ({url}) => {
  console.log(url);
  const { products } = useFetch(url)

  return (
      <BigList products={products}/>
  )
}

const BigList = React.memo(({ products}) => {
  useEffect(() => {
    console.count('hello from big list');
  });

  return (
    <section className='products'>
      {products.map((product) => {
        return (
          <SingleProduct
            key={product.item_id}
            index={product.item_id}
            item_name= {product.item_name}
            base_price ={product.base_price}
            description= {product.description}
            source_address = {product.source_address}
            date = {product.datetime}
            destination_address = {product.destination_address}
            on_sale = {product.on_sale}
          ></SingleProduct>
        )
      })}
    </section>
  )
})

const SingleProduct = ({ index,item_name,base_price,description,source_address,destination_address,on_sale,date}) => {

  let style  = {}
  if(!on_sale) {
    style = {backgroundColor : "grey"}
  }


    const [bid, setBid] = useState({
        id : 0,
        price : 0
    })

    const [submitted, setSubmitted] = useState(false);



    useEffect(() => {
      bid.id = index
    });

    return (
        <div className="col-md-12">
            <div style={style} className="card card-container">
                <article className='product'>
                <img src="https://www.shareicon.net/data/128x128/2015/09/23/106279_cart_512x512.png" alt={item_name} />
                <h4>{item_name}</h4>
                <p>{description}</p>
                <p>{source_address}</p>
                <p>{destination_address}</p>
                <p>{date[0-8]}</p>
                <p>Rs {base_price}</p>

                </article>
            </div>
        </div>
  )
}
export default UserItems
